mapper.update:
UPDATE attendance
SET check_in = #{checkIn}
WHERE lecture_id = #{lectureId} and student_id = #{studentId};

UPDATE attendance
SET assignment = #{assignment}, done_date = sysdate()
WHERE lecture_id = #{lectureId} and student_id = #{studentId};

UPDATE attendance
SET test_score = #{testScore}
WHERE lecture_id = #{lectureId} and student_id = #{studentId};
